useEffect(() => {
  const finalPath = diskPath || localStorage.getItem("selectedDiskPath");
  if (finalPath) {
    fetch(`http://localhost:3001/partitions?path=${encodeURIComponent(finalPath)}`)
      .then((res) => res.json())
      .then((data) => setPartitions(data))
      .catch(() => setPartitions([]));
  } else {
    navigate("/discos"); // Redirige si no hay path disponible
  }
}, [diskPath]);

useEffect(() => {
  if (diskPath) {
    localStorage.setItem("selectedDiskPath", diskPath);
  }
}, [diskPath]);
