import React, { useEffect, useState } from "react";
import { useParams, useNavigate, useLocation } from "react-router-dom";

function FileViewer() {
  const { id } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const [tree, setTree] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/filesystem/${id}`)
      .then((res) => res.json())
      .then((data) => setTree(data))
      .catch((err) => {
        console.error("Error al obtener √°rbol:", err);
        setTree(null);
      });
  }, [id]);

  const renderNode = (node, depth = 0) => {
    const isFolder = node.type === "directory";
    return (
      <div key={node.name + node.type} style={{ marginLeft: depth * 20 }}>
        <span style={{ fontWeight: isFolder ? "bold" : "normal" }}>
          {isFolder ? "üìÅ" : "üìÑ"} {node.name}
        </span>
        {isFolder &&
          node.children?.map((child) => renderNode(child, depth + 1))}
      </div>
    );
  };

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h2 style={styles.title}>Estructura del Sistema de Archivos</h2>
        {tree ? (
          <div>{renderNode(tree)}</div>
        ) : (
          <p style={{ color: "#888" }}>Cargando estructura...</p>
        )}
        <button onClick={() => navigate("/discos")} style={styles.backBtn}>
          Volver a Discos
        </button>
      </div>
    </div>
  );
}

const styles = {
  page: {
    minHeight: "100vh",
    backgroundColor: "#f2f2f2",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    fontFamily: "Segoe UI, sans-serif",
    padding: "2rem",
  },
  card: {
    backgroundColor: "#fff",
    padding: "2rem",
    borderRadius: "10px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
    width: "90%",
    maxWidth: "800px",
    overflowY: "auto",
    maxHeight: "90vh",
  },
  title: {
    fontSize: "1.6rem",
    marginBottom: "1rem",
  },
  backBtn: {
    marginTop: "2rem",
    padding: "0.6rem 1.2rem",
    backgroundColor: "#444",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontWeight: "bold",
  },
};

export default FileViewer;
