import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

function FileViewer() {
  const { id } = useParams();
  const [tree, setTree] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`http://localhost:3001/filesystem/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error("No se pudo obtener el sistema de archivos.");
        return res.json();
      })
      .then((data) => setTree(data))
      .catch((err) => setError(err.message));
  }, [id]);

  const renderNode = (node, depth = 0) => {
    return (
      <div key={node.name} style={{ marginLeft: `${depth * 20}px`, marginTop: "4px" }}>
        <span style={{ fontWeight: node.type === "folder" ? "bold" : "normal" }}>
          üìÅ {node.name}
        </span>
        {node.children &&
          node.children.map((child) => renderNode(child, depth + 1))}
      </div>
    );
  };

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h2>Explorador del Sistema de Archivos</h2>
        <button onClick={() => navigate("/discos")} style={styles.backBtn}>
          Volver al men√∫
        </button>
        <div style={styles.content}>
          {error && <p style={{ color: "red" }}>{error}</p>}
          {tree ? renderNode(tree) : <p>Cargando estructura...</p>}
        </div>
      </div>
    </div>
  );
}

const styles = {
  page: {
    minHeight: "100vh",
    backgroundColor: "#f4f4f4",
    display: "flex",
    justifyContent: "center",
    alignItems: "flex-start",
    paddingTop: "3rem",
    fontFamily: "Segoe UI, sans-serif",
  },
  card: {
    backgroundColor: "#fff",
    padding: "2rem",
    borderRadius: "10px",
    boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
    width: "80%",
    maxWidth: "800px",
  },
  content: {
    marginTop: "1rem",
    backgroundColor: "#f9f9f9",
    padding: "1rem",
    borderRadius: "6px",
    maxHeight: "600px",
    overflowY: "auto",
    whiteSpace: "pre-wrap",
  },
  backBtn: {
    marginBottom: "1rem",
    backgroundColor: "#444",
    color: "white",
    border: "none",
    padding: "0.5rem 1rem",
    borderRadius: "6px",
    cursor: "pointer",
  },
};

export default FileViewer;
