// src/DiskSelector.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function DiskSelector() {
  const [disks, setDisks] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:3001/disks")
      .then((res) => res.json())
      .then((data) => setDisks(data))
      .catch((err) => console.error("Error al cargar discos:", err));
  }, []);

  const handleSelect = (disk) => {
    navigate(`/viewer/${disk.name}`, { state: { disk } });
  };

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h2 style={styles.title}>Visualizador del Sistema de Archivos</h2>
        <p style={styles.subtitle}>Seleccione el disco que desea visualizar:</p>
        <div style={styles.grid}>
          {disks.map((disk) => (
            <div
              key={disk.name}
              style={styles.diskCard}
              onClick={() => handleSelect(disk)}
            >
              <img
                src="/disk-icon.png"
                alt="Disco"
                style={styles.diskImage}
              />
              <h3>{disk.name}</h3>
              <p><strong>Capacidad:</strong> {disk.size}</p>
              <p><strong>Fit:</strong> {disk.fit}</p>
              <p>
                <strong>Particiones:</strong>{" "}
                {disk.mounted_partitions.join(", ") || "Ninguna"}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

const styles = {
  page: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    minHeight: "100vh",
    backgroundColor: "#f4f4f4",
    fontFamily: "Segoe UI, sans-serif",
  },
  card: {
    backgroundColor: "#ffffff",
    padding: "2.5rem",
    borderRadius: "12px",
    boxShadow: "0 4px 16px rgba(0,0,0,0.1)",
    width: "90%",
    maxWidth: "800px",
    textAlign: "center",
  },
  title: {
    fontSize: "1.8rem",
    marginBottom: "0.5rem",
  },
  subtitle: {
    marginBottom: "1.5rem",
    color: "#444",
  },
  grid: {
    display: "flex",
    justifyContent: "center",
    flexWrap: "wrap",
    gap: "1.5rem",
  },
  diskCard: {
    backgroundColor: "#eafafa",
    borderRadius: "10px",
    padding: "1rem",
    cursor: "pointer",
    width: "160px",
    boxShadow: "0 3px 8px rgba(0,0,0,0.1)",
    transition: "transform 0.2s",
  },
  diskImage: {
    width: "48px",
    marginBottom: "0.8rem",
  },
};

export default DiskSelector;
