import React, { useState, useEffect, useRef } from "react";
import Editor from "@monaco-editor/react";
import "./App.css";
import LoginForm from "./LoginForm";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import DiskSelector from "./DiskSelector";
import DiskViewer from "./DiskViewer";
import { useNavigate } from "react-router-dom";


function App() {
  const [commandInput, setCommandInput] = useState("");
  const [output, setOutput] = useState("");
  const textareaRef = useRef(null);
  const [usuarioActual, setUsuarioActual] = useState(null);
  const [mostrarLogin, setMostrarLogin] = useState(false);
  const navigate = useNavigate();


  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = "auto";
      textarea.style.height = `${textarea.scrollHeight}px`;
    }
  }, [output]);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file && file.name.endsWith(".smia")) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setCommandInput(event.target.result);
      };
      reader.readAsText(file);
    } else {
      alert("Por favor selecciona un archivo .smia válido");
    }
  };

  const handleExecute = async () => {
    try {
      const response = await fetch("http://localhost:3001/execute", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          command: commandInput,
          user: usuarioActual?.username,
          partitionId: usuarioActual?.partitionId,
        }),
      });

      const result = await response.json();
      setOutput(result.output);
    } catch (error) {
      setOutput("Error al comunicarse con el backend.");
    }
  };

  const handleClear = () => {
    setCommandInput("");
    setOutput("");
  };

  const handleLogout = () => {
    setUsuarioActual(null);
    setCommandInput("");
    setOutput("");
  };

  return (
    <Router>
      <Routes>
        {/* Ruta principal: editor de comandos */}
        <Route
          path="/"
          element={
            <div className="App">
              <header className="App-header">
                <h1>Sistema de Archivos EXT2/EXT3 - Proyecto MIA</h1>
                {usuarioActual && (
                  <p>
                    Sesión activa: <strong>{usuarioActual.username}</strong> (ID:{" "}
                    {usuarioActual.partitionId})
                  </p>
                )}
              </header>

              <div className="controls">
                <input type="file" accept=".smia" onChange={handleFileUpload} />
                <button onClick={handleExecute}>Ejecutar</button>
                <button onClick={handleClear}>Limpiar</button>
                {!usuarioActual ? (
                  <button
                    onClick={() => setMostrarLogin(true)}
                    style={{ backgroundColor: "#2e7d32", color: "white" }}
                  >
                    Iniciar Sesión
                  </button>
                ) : (
                  <button
                    onClick={handleLogout}
                    style={{ backgroundColor: "#880e4f", color: "white" }}
                  >
                    Cerrar Sesión ({usuarioActual.username})
                  </button>
                )}
              </div>

              <div className="editor-container">
                <div className="editor">
                  <label>Entrada:</label>
                  <Editor
                    height="560px"
                    language="plaintext"
                    theme="hc-black"
                    value={commandInput}
                    onChange={(value) => setCommandInput(value)}
                    options={{
                      minimap: { enabled: false },
                      fontSize: 14,
                      lineNumbers: "on",
                      scrollBeyondLastLine: false,
                      wordWrap: "on",
                    }}
                  />
                </div>

                <div className="editor">
                  <label>Salida:</label>
                  <textarea
                    ref={textareaRef}
                    className="salida"
                    value={output}
                    readOnly
                    placeholder="#------Estiben Yair Lopez Leveron------ 202204578----"
                  />
                </div>
              </div>

              {/* Modal de Login con overlay */}
              {mostrarLogin && (
                <>
                  <div
                    style={{
                      position: "fixed",
                      top: 0,
                      left: 0,
                      width: "100vw",
                      height: "100vh",
                      backgroundColor: "rgba(0, 0, 0, 0.5)",
                      zIndex: 999,
                    }}
                  />
                  <div
                    style={{
                      position: "fixed",
                      top: "30%",
                      left: "50%",
                      transform: "translate(-50%, -50%)",
                      background: "#ffffff",
                      padding: "2rem",
                      borderRadius: "10px",
                      boxShadow: "0 0 15px rgba(0,0,0,0.3)",
                      zIndex: 1000,
                    }}
                  >
<LoginForm
  onLogin={(info) => {
    setUsuarioActual(info);
    setMostrarLogin(false);

    // Redirigir automáticamente al visualizador del disco
    navigate(`/viewer/${info.partitionId}`, {
      state: {
        disk: {
          name: info.partitionId,
          partition_id: info.partitionId,
          mounted_partitions: [],
          size: "Desconocido",
          fit: "Desconocido",
          path: "Desconocido",
        },
      },
    });
  }}
/>

                    <button
                      onClick={() => setMostrarLogin(false)}
                      style={{
                        marginTop: "1rem",
                        backgroundColor: "#999",
                        color: "#fff",
                        padding: "0.4rem 1rem",
                        border: "none",
                        borderRadius: "5px",
                        cursor: "pointer",
                      }}
                    >
                      Cancelar
                    </button>
                  </div>
                </>
              )}
            </div>
          }
        />

        {/* Ruta para selección de discos */}
        <Route path="/discos" element={<DiskSelector />} />

        {/* Ruta para ver estructura del disco */}
        <Route path="/viewer/:nombre" element={<DiskViewer />} />
      </Routes>
    </Router>
  );
}

export default App;
